import React, { useState } from 'react'

const Button = props => {
    const buttonCss = {
        normal:{
            padding: '15px 40px',
            fontSize: '20px',
            background: props.colors[3][1],
            color: props.colors[4][1],
            border: 0,
            transition: 'all 0.1s'
        },
        hover: {
            color: props.colors[3][1],
            background: props.colors[2][1],
        }
    }
    const [hover, setHover] = useState(false);
    return (
        <button 
            onMouseEnter={()=>{
              setHover(true);
            }}
            onMouseLeave={()=>{
              setHover(false);
            }}
            style={{
              ...buttonCss.normal,
              ...(hover ? buttonCss.hover : null)
            }} >{props.text}</button>
    )
}

const Content = props => {
    const jumboCss = {
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        backgroundColor: props.colors[2][1],
        color: props.colors[4][1],
        height: '60vh',
    }
    const colorBoxes = props.colors.map((c, index) => {
          const textColor = index > 1 ? props.colors[0][1] : props.colors[4][1]
          return (
              <div key={c[0]} className="color" style={{
                height: '200px',
                flex: 1,

                backgroundColor: c[1],
              }}>
              <p style={{
                lineHeight: '200px',
                color: textColor
              }}> 
                {c[0]}
              </p>
            </div>
            )
        })
    return (
        <div style={jumboCss}>
            <div style={{
            flexBasis: '50px',

            }} >

                <Button text={'contribute'} colors={props.colors} />
            </div>
            <h1 style={{
                flex: 1,
                fontFamily: props.font
            }} className="mainTitle">
                Content will go here
            </h1>
            <h2 className="mainsubtitle" style={{
                flex: 1,
                fontFamily: props.font === "'Major Mono Display', monospace" ? "'Anonymous Pro', monospace" : "'Major Mono Display', monospace"
            }} >
                a gallery or somsuch
            </h2>
            <div style={{
                flex:1,
                display: 'flex',
                flexDirection: 'row',
            }} >
            {colorBoxes}
            </div>
        </div>
    )
}
export default Content
