import React, { useState } from 'react';
import './App.css';
import Sidebar from './components/sidebar'
import Jumbo from './components/jumbo';

export const colors = [
  [
      ['building1','#2D2032'],
      ['building2','#142454'],
      ['building3','#765D8F'],
      ['building4','#B14060'],
      ['building5','#CCABAC']
  ],
  [
      ['sunset1', '#44413D'],
      ['sunset2', '#554A4A'],
      ['sunset3', '#946763'],
      ['sunset4', '#BB6662'],
      ['sunset5', '#EDB087']
  ],
  [
      ['jen1', '#110B12'],
      ['jen2', '#4C203A'],
      ['jen3', '#5C6162'],
      ['jen4', '#B75E7D'],
      ['jen5', '#E4CCE1']
  ],
  [
      ['sky1', '#2C324A'],
      ['sky2', '#53717B'],
      ['sky3', '#688681'],
      ['sky4', '#819D89'],
      ['sky5', '#E1C8A6']
  ]
]


const ColorSelector = ({setPalette}) => {
  const selectCss = {
    position: 'fixed',
    top: '10px',
    right: '10px',

  }
  return (
  <select style={selectCss} onChange={e => setPalette(e.target.value)}>
      <option value="0">building</option>
      <option value="1">sunset</option>
      <option value="2">jen</option>
      <option value="3">sky</option>
  </select>
)
}

function App() {
  const [ palette, setPalette ] = useState(0)
  const colorBoxes = colors.map((ca, index) => {
    const boxes = ca.map((c, index) => {
      const textColor = index > 0 ? ca[0][1] : ca[4][1]
      return (

          <div key={c[0]} className="color" style={{
            height: '200px',
            flex: 1,
            backgroundColor: c[1],
          }}>
          <p style={{
            lineHeight: '200px',
            color: textColor
          }}> 
            {c[0]}
          </p>
        </div>
        )
    })
    return <div key={index} className="palette" id="building" style={flexCss}>{boxes}</div>
  })
  return (
    <div className="App">
      <ColorSelector setPalette={setPalette} />
      <Sidebar colors={colors[palette]} />
      <Jumbo colors={colors[palette]} />
      <div style={{flex: 1}}>

      </div>
    </div>
  );
}
const flexCss = {
  flex: 1,
  overflow: 'hidden',
  display: 'flex',
  width: '80%',
  margin: 'auto'
}

export default App;
