import React, { useState } from 'react';
import './App.css';
import Sidebar from './components/sidebar'
import Jumbo from './components/jumbo';
import Content from './components/content';

export const colors = [
  [
      ['building1','#2D2032'],
      ['building2','#142454'],
      ['building3','#765D8F'],
      ['building4','#B14060'],
      ['building5','#CCABAC']
  ],
  [
      ['sunset1', '#44413D'],
      ['sunset2', '#554A4A'],
      ['sunset3', '#946763'],
      ['sunset4', '#BB6662'],
      ['sunset5', '#EDB087']
  ],
  [
      ['jen1', '#110B12'],
      ['jen2', '#4C203A'],
      ['jen3', '#5C6162'],
      ['jen4', '#B75E7D'],
      ['jen5', '#E4CCE1']
  ],
  [
      ['sky1', '#2C324A'],
      ['sky2', '#53717B'],
      ['sky3', '#688681'],
      ['sky4', '#819D89'],
      ['sky5', '#E1C8A6']
  ]
]

const fonts = [
  "'Vast Shadow', cursive",
  "'Anonymous Pro', monospace",
  "'Major Mono Display', monospace",
]

const FontSelector = ({setFont}) => {
  const selectCss = {
    position: 'fixed',
    top: '15px',
    right: '150px',
    zIndex: 100,
  }
  const changer = v => {
    console.log(fonts[v])
    setFont(v)
  }
  return (
    <select style={selectCss} onChange={e => changer(e.target.value)}>
        <option value="0">Vast Shadow</option>
        <option value="1">Anonymous Pro</option>
        <option value="2">Major Mono Display</option>
    </select>
  )
}

const ColorSelector = ({setPalette}) => {
  const selectCss = {
    position: 'fixed',
    top: '50px',
    right: '150px',
    zIndex: 100,
  }
  return (
    <div style={selectCss}>
      <select onChange={e => setPalette(e.target.value)}>
          <option value="0">building</option>
          <option value="1">sunset</option>
          <option value="2">jen</option>
          <option value="3">sky</option>
      </select>
    </div>
  )
}

function App() {
  const [ font, setFont ] = useState(0)
  const [ palette, setPalette ] = useState(0)

  return (
    <div className="App">
      <ColorSelector setPalette={setPalette} />
      <FontSelector setFont={setFont} />
      <Sidebar colors={colors[palette]} link={'learn'}  />
      <div style={bodyCss}>

      <Jumbo colors={colors[palette]} font={fonts[font]} />
      <Content colors={colors[palette]} font={fonts[font]} />
      </div>
      <Sidebar colors={colors[palette]} link={'contribute'} />
    </div>
  );
}
const flexCss = {
  flex: 1,
  overflow: 'hidden',
  display: 'flex',
  width: '80%',
  margin: 'auto'
}

const bodyCss = {
  flex: 1,
  display: 'flex',
  flexDirection: 'column',
}

export default App;
