import React, { useState } from 'react';
import './App.css';
import Sidebar from './components/sidebar'

export const colors = [
  [
      ['Dark Slate Gray','#2D2032'],
      ['Midnight Blue','#142454'],
      ['Slate Gray','#765D8F'],
      ['Old Rose','#B14060'],
      ['Rosy Brown','#CCABAC']
  ],
  [
      ['Dim Gray', '#44413D'],
      ['Dim Gray', '#554A4A'],
      ['Gray', '#946763'],
      ['Indian Red', '#BB6662'],
      ['Dark Salmon', '#EDB087']
  ],
  [
      ['Dark Slate Gray', '#110B12'],
      ['Brown', '#4C203A'],
      ['Slate Gray', '#5C6162'],
      ['Indian Red', '#B75E7D'],
      ['Thistle', '#E4CCE1']
  ],
  [
      ['Dark Slate Gray', '#2C324A'],
      ['Slate Gray', '#53717B'],
      ['Slate Gray', '#688681'],
      ['Dark Sea Green', '#819D89'],
      ['Burly Wood', '#E1C8A6']
  ]
]


const ColorSelector = ({setPalette}) => {
  const selectCss = {
    position: 'fixed',
    top: '10px',
    right: '10px',

  }
  return (
  <select style={selectCss} onChange={e => setPalette(e.target.value)}>
      <option value="0">building</option>
      <option value="1">sunset</option>
      <option value="2">jen</option>
      <option value="3">sky</option>
  </select>
)
}

function App() {
  const [ palette, setPalette ] = useState(0)
  const colorBoxes = colors.map((ca, index) => {
    const boxes = ca.map((c, index) => {
      const textColor = index > 0 ? ca[0][1] : ca[4][1]
      return (

          <div key={c[0]} className="color" style={{
            height: '200px',
            flex: 1,
            backgroundColor: c[1],
          }}>
          <p style={{
            lineHeight: '200px',
            color: textColor
          }}> 
            {c[0]}
          </p>
        </div>
        )
    })
    return <div key={index} className="palette" id="building" style={flexCss}>{boxes}</div>
  })
  return (
    <div className="App">
      <ColorSelector setPalette={setPalette} />
      <Sidebar colors={colors[palette]} />
      <div style={{flex: 1}}>

      {colorBoxes}
      </div>
    </div>
  );
}
const flexCss = {
  flex: 1,
  overflow: 'hidden',
  display: 'flex',
  width: '80%',
  margin: 'auto'
}

export default App;
