import React, { useState } from 'react'


const boxCss = {
  width: '350px',
  height: '350px',
  margin: '10px',
  backgroundSize: 'cover',
  backgroundPosition: 'center',
  transition: 'all 1s',
}

const textBoxCss = {
    transition: 'all 0.1s',
    width: '80%',

    hover: {
        background: 'white',
    }
}

const Box = (props) => {
    const [hover, setHover] = useState(false);
    const boxProps = props.boxProps
    console.log(props.colors)
    require(`../images/digcolls/${boxProps.image}`)
    return (
        <div 
            className="box" 
            style={{
                background: `url('/images/digcolls/${boxProps.image}')`, 
                border: `40px solid ${props.colors[3][1]}`,
                ...boxCss,
            }}
            onMouseEnter={()=>{
                setHover(true);
            }}
            onMouseLeave={()=>{
                setHover(false);
            }} 
        >
            <div style={{...textBoxCss, ...(hover ? textBoxCss.hover : null)}}>
                {boxProps.title}
            </div>
        </div>
    )
}

export default Box
