import React, { useState } from 'react'

const Jumbo = props => {
    const jumboCss = {
        flexBasis: '60vh',
        backgroundColor: props.colors[3][1],
        color: props.colors[4][1],
        height: '60vh',
        display: 'flex',
        flexDirection: 'column',
    }
    return (
        <div style={jumboCss}>
            <h1 style={{
                fontFamily: props.font
            }} className="mainTitle">
                Digital Newberry
            </h1>
            <h2 className="mainsubtitle" style={{
                fontFamily: props.font === "'Major Mono Display', monospace" ? "'Anonymous Pro', monospace" : "'Major Mono Display', monospace"
            }} >
                Newberry Department of Digital Initiatives and Services
            </h2>
            <div style={{
            flexBasis: '50px',

            }} >

            <Button text={'learn'} colors={props.colors} />
            </div>
        </div>
    )
}

const Button = props => {
    const buttonCss = {
        normal:{
            padding: '15px 40px',
            position: 'relative',
            left: 0,
            fontSize: '20px',
            background: props.colors[4][1],
            color: props.colors[2][1],
            border: 0,
            transition: 'all 0.1s',
        },
        hover: {
            // left: '-50px',
            padding: '15px 40px 15px 100px',
            color: props.colors[4][1],
            background: props.colors[2][1],
        }
    }
    const [hover, setHover] = useState(false);
    return (
        <button 
            onMouseEnter={()=>{
              setHover(true);
            }}
            onMouseLeave={()=>{
              setHover(false);
            }}
            style={{
              ...buttonCss.normal,
              ...(hover ? buttonCss.hover : null)
            }} >{props.text}</button>
    )
}
export default Jumbo
